import Astronomy from '../../components/Astronomy'
import React from 'react'
import { getData } from '../../actions'
import { connect } from 'react-redux'

class VisibleAstronomy extends React.Component {
    componentDidMount(){
        this.props.getData()
    }
    render(){
        const {astronomy, error} = this.props
        return (
            <Astronomy astronomy={astronomy} error={error} />
        )
    }
}

const mapStateToProps = state => ({
    astronomy: state.astronomy,
    error: state.astronomy.error,
})

const mapDispatchToProps = dispatch => ({
    getData: () => dispatch(getData())
})

export default connect(mapStateToProps, mapDispatchToProps)(VisibleAstronomy)
